 * For more details take a look at the Java Libraries chapter in the Gradle
 * User Manual available at https://docs.gradle.org/5.6.4/userguide/java_library_plugin.html
 */

repositories {
    // Use jcenter for resolving dependencies.
    // You can declare any Maven/Ivy/file repository here.
    google()
    jcenter()
    mavenCentral()
}

plugins {
    // Apply the application plugin to add support for Main class
    application

    // Apply the java-library plugin to add support for Java Library
    `java-library`
}

application {
    mainClassName = "app.btcore.java.MainApplication"
}

dependencies {
    api("org.apache.commons:commons-math3:3.6.1")
    implementation("com.google.guava:guava:28.0-jre")

    // Adds GSON for request and response serialization
    implementation("com.google.code.gson:gson:2.8.6")

    // Adds support for dotenv configuration files
    implementation("io.github.cdimascio:java-dotenv:5.1.3")

    // Adds OkHttp and Retrofit for our HTTP requests
    compile("com.squareup.okhttp3:okhttp:3.14.4")
    implementation("com.squareup.retrofit2:retrofit:2.6.2") {
        exclude(module = "okhttp")
    }
    implementation("com.squareup.retrofit2:converter-gson:2.6.2") {
        exclude(module = "gson")
    }

    testImplementation("junit:junit:4.12")
}


tasks {
    test {
        testLogging {
          showStandardStreams = true
        }
    }
}
